-- Name: add_line_if_not_exist(text, text, text, text); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.add_line_if_not_exist(text, text, text, text) RETURNS boolean
    LANGUAGE plpgsql
    AS $_$
DECLARE tabName ALIAS FOR $1;
DECLARE columnName ALIAS FOR $2;
DECLARE fieldValue ALIAS FOR $3;
DECLARE insertcmd ALIAS FOR $4;

DECLARE found integer;
BEGIN
    EXECUTE 'SELECT count(*) FROM ' || tabName || ' WHERE ' ||  columnName || ' = ' || fieldValue
           INTO found;

    IF found = 0 THEN
        EXECUTE insertcmd ;
        return true;
    END IF;
    return false;
END;
$_$;


--
-- Name: alter_column(text, text, text, text); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.alter_column(text, text, text, text) RETURNS boolean
    LANGUAGE plpgsql
    AS $_$
DECLARE tabName ALIAS FOR $1;
DECLARE colName ALIAS FOR $2;
DECLARE colDef ALIAS FOR $3;
DECLARE colComment ALIAS FOR $4;
DECLARE found integer;
BEGIN
    select count(*) into found from information_schema.columns where table_name = tabName and column_name = colName;
    IF found > 0 THEN
        EXECUTE 'ALTER TABLE '|| tabName || ' ALTER COLUMN ' || colName || ' ' || colDef;
        EXECUTE 'COMMENT ON COLUMN '|| tabName || '.' || colName || ' IS ' || colComment;
        return true;
    END IF;
    return false;
END;

$_$;


--
-- Name: check_polymorphism_business_contracts(); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.check_polymorphism_business_contracts() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE v_commissioned_id integer;
DECLARE v_commissioned_type varchar(70);
DECLARE found integer;
DECLARE Message varchar(150);
BEGIN
    IF (TG_OP = 'UPDATE' or TG_OP = 'INSERT') THEN
        v_commissioned_type = NEW.commissioned_type;
        v_commissioned_id = NEW.commissioned_id;

        IF v_commissioned_type = 'Goxygene::Consultant' THEN
            select count(*) into found from goxygene_consultants where id = v_commissioned_id;
        ELSIF v_commissioned_type = 'Goxygene::Subcontractor' THEN
            select count(*) into found from goxygene_subcontractors where id = v_commissioned_id;

        ELSE
        	Message = 'Value of commissioned_type' || v_commissioned_type || ' is not managed';
            RAISE EXCEPTION '%', Message;
        END IF;

        IF found = 0 THEN
        	Message = 'Biller ' || v_commissioned_type || ' ' || cast(v_commissioned_id as varchar(70)) || ' not found';
            RAISE EXCEPTION '%', Message;
        END IF;
    END IF;
    return NEW;
END;
$$;


