--
-- Name: create_and_comment_column_with_drop_if_exists(text, text, text, text); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.create_and_comment_column_with_drop_if_exists(text, text, text, text) RETURNS boolean
    LANGUAGE plpgsql
    AS $_$
DECLARE tabName ALIAS FOR $1;
DECLARE colName ALIAS FOR $2;
DECLARE colDef ALIAS FOR $3;
DECLARE colComment ALIAS FOR $4;
DECLARE found integer;
BEGIN
    select count(*) into found from information_schema.columns where table_name = tabName and column_name = colName;
    IF found > 0 THEN
        EXECUTE 'ALTER TABLE '|| tabName || ' DROP COLUMN ' || colName || ' CASCADE';
    END IF;
    EXECUTE 'ALTER TABLE '|| tabName || ' ADD COLUMN ' || colName || ' ' || colDef;
    EXECUTE 'COMMENT ON COLUMN '|| tabName || '.' || colName || ' IS ' || colComment;
