    LANGUAGE plpgsql
    AS $$
DECLARE
    tname TEXT;
    cur_tables CURSOR FOR select table_name from information_schema."tables"
                                                                where table_schema = 'public' and table_name not like 'ar_intern%' and table_name not like 'oauth_%' and table_name <> 'cas_authentications'  and table_name <> 'audits' and table_name <> 'casino_users' and table_name not like 'goxygene_%'
                                                                order by table_name;
BEGIN
   -- Open the cursor
   OPEN cur_tables;

   LOOP
    -- fetch row into the film
      FETCH cur_tables INTO tname;
    -- exit when no more row to fetch
      EXIT WHEN NOT FOUND;

    -- build the output
      EXECUTE 'ALTER TABLE '|| tname || ' RENAME TO ' || 'goxygene_' || tname ;
   END LOOP;

   -- Close the cursor
   CLOSE cur_tables;

   RETURN true;
END; $$;


