-- Name: vw_annees_mois_conseillers_gestion; Type: VIEW; Schema: public; Owner: -
--

CREATE VIEW public.vw_annees_mois_conseillers_gestion AS
 SELECT req_cc.user_id,
    req_cc.annee,
    req_cc.mois
   FROM ( SELECT goxygene_business_contract_versions.created_by AS user_id,
            date_part('year'::text, goxygene_business_contract_versions.created_at) AS annee,
            date_part('month'::text, goxygene_business_contract_versions.created_at) AS mois
           FROM public.goxygene_business_contract_versions
          GROUP BY goxygene_business_contract_versions.created_by, goxygene_business_contract_versions.created_at) req_cc
  GROUP BY req_cc.user_id, req_cc.annee, req_cc.mois
UNION
 SELECT req_demfact.user_id,
    req_demfact.annee,
    req_demfact.mois
   FROM ( SELECT goxygene_office_customer_bills.updated_by AS user_id,
            date_part('year'::text, goxygene_office_customer_bills.updated_at) AS annee,
            date_part('month'::text, goxygene_office_customer_bills.updated_at) AS mois
           FROM public.goxygene_office_customer_bills
          WHERE (goxygene_office_customer_bills.status = 'query_granted'::public.customer_bill_status_enum)
          GROUP BY goxygene_office_customer_bills.updated_by, goxygene_office_customer_bills.updated_at) req_demfact
  GROUP BY req_demfact.user_id, req_demfact.annee, req_demfact.mois
UNION
 SELECT req_fact.user_id,
    req_fact.annee,
    req_fact.mois
   FROM ( SELECT goxygene_customer_bills.created_by AS user_id,
            date_part('year'::text, goxygene_customer_bills.created_at) AS annee,
            date_part('month'::text, goxygene_customer_bills.created_at) AS mois
           FROM public.goxygene_customer_bills
          GROUP BY goxygene_customer_bills.created_by, goxygene_customer_bills.created_at) req_fact
  GROUP BY req_fact.user_id, req_fact.annee, req_fact.mois
UNION
 SELECT req_ndf.user_id,
    req_ndf.annee,
    req_ndf.mois
   FROM ( SELECT goxygene_expense_reports.created_by AS user_id,
            date_part('year'::text, goxygene_expense_reports.created_at) AS annee,
            date_part('month'::text, goxygene_expense_reports.created_at) AS mois
           FROM public.goxygene_expense_reports
          GROUP BY goxygene_expense_reports.created_by, goxygene_expense_reports.created_at) req_ndf
  GROUP BY req_ndf.user_id, req_ndf.annee, req_ndf.mois
UNION
 SELECT req_ndf.user_id,
    req_ndf.annee,
