            ELSE ('C'::text || "left"("right"("substring"("substring"(goxygene_timeline_items.comment, "position"(goxygene_timeline_items.comment, '"potential"'::text)), 15), 3), 1))
        END AS new_potential,
    goxygene_timeline_items.date AS date_changement
