CREATE VIEW public.vw_consultants AS
 SELECT consult.consultant_status AS status,
    prospect.gitg_id AS id_prospect,
    individuals.last_name AS nom,
    individuals.first_name AS prenom,
    consult.advisor_employee_id AS id_conseiller_trans,
    consult.correspondant_employee_id AS id_conseiller_gest,
    prospect.created_at AS prosp_create_date,
    items.date AS date_recrutement,
    consult.id AS id_consultant,
        CASE
            WHEN (consult.consultant_status = 'consultant'::public.consultant_status_enum) THEN 'Oui'::text
            ELSE 'Non'::text
        END AS actif,
    consult.expected_fees AS prem_command,
    consult.expected_fees_corrected_amount AS pred_commmand_retraite,
    consult.competences,
    civilities.sex AS sexe,
    individuals.birth_date AS date_naissance,
        CASE
            WHEN ((coordos.zip_code_id IS NULL) AND (NOT (coordos.zip_code IS NULL))) THEN ( SELECT goxygene_regions.label
               FROM public.goxygene_regions
              WHERE (goxygene_regions.id = ( SELECT goxygene_departments.region_id
                       FROM public.goxygene_departments
                      WHERE (((goxygene_departments.zip_code)::text = "left"((coordos.zip_code)::text, 2)) OR ((goxygene_departments.zip_code)::text = "left"((coordos.zip_code)::text, 3))))))
            ELSE regions.label
        END AS region,
        CASE
            WHEN ((coordos.zip_code_id IS NULL) AND (NOT (coordos.zip_code IS NULL))) THEN ( SELECT goxygene_departments.label
               FROM public.goxygene_departments
              WHERE (((goxygene_departments.zip_code)::text = "left"((coordos.zip_code)::text, 2)) OR ((goxygene_departments.zip_code)::text = "left"((coordos.zip_code)::text, 3)) OR ((goxygene_departments.zip_code)::text = "left"((coordos.zip_code)::text, 5))))
            ELSE departments.label
        END AS departement,
    coordos.email,
    coordos.mobile_phone,
    coordos.phone,
    consult.b2b,
    ('C'::text || ((prospect.potential)::character varying(1))::text) AS qualite
   FROM (((((((((public.goxygene_consultants consult
     LEFT JOIN public.goxygene_prospecting_data prospect ON ((consult.id = prospect.consultant_id)))
     JOIN public.goxygene_individuals individuals ON ((consult.individual_id = individuals.id)))
     LEFT JOIN public.goxygene_timeline_items items ON (((consult.id = items.consultant_id) AND (items.timeline_type_id = 61))))
     LEFT JOIN public.goxygene_timeline_items pitems ON (((consult.id = pitems.consultant_id) AND (items.timeline_type_id = 21))))
     LEFT JOIN public.goxygene_civilities civilities ON ((individuals.civility_id = civilities.id)))
     LEFT JOIN public.goxygene_contact_data coordos ON ((consult.contact_datum_id = coordos.id)))
     LEFT JOIN public.goxygene_zip_codes zips ON ((coordos.zip_code_id = zips.id)))
     LEFT JOIN public.goxygene_departments departments ON ((zips.department_id = departments.id)))
     LEFT JOIN public.goxygene_regions regions ON ((departments.region_id = regions.id)));


