            (COALESCE(sum(COALESCE(req.frais_postaux, (0)::numeric)), (0)::numeric))::numeric(15,2) AS frais_postaux,
            (COALESCE(sum(COALESCE(req.telecom, (0)::numeric)), (0)::numeric))::numeric(15,2) AS telecom,
            (COALESCE(sum(COALESCE(req.autres, (0)::numeric)), (0)::numeric))::numeric(15,2) AS autres,
