            req.ht,
            req.tva
           FROM ( SELECT reports.consultant_id AS id_consult,
                    date_part('year'::text, reports.date) AS annee,
                    date_part('month'::text, reports.date) AS mois,
                        CASE
                            WHEN (details.expense_type_id = 6) THEN (COALESCE(sum(COALESCE(details.kilometers, 0)), (0)::bigint))::numeric(15,2)
                            ELSE (0)::numeric
                        END AS kms,
                        CASE
                            WHEN (details.expense_type_id = ANY (ARRAY[6, 7])) THEN (COALESCE(sum(COALESCE(details.amount, (0)::numeric)), (0)::numeric))::numeric(15,2)
                            ELSE (0)::numeric
                        END AS deplacements,
                        CASE
                            WHEN (details.expense_type_id = 1) THEN (COALESCE(sum(COALESCE(details.amount, (0)::numeric)), (0)::numeric))::numeric(15,2)
                            ELSE (0)::numeric
                        END AS bureautique,
                        CASE
                            WHEN (details.expense_type_id = 2) THEN (COALESCE(sum(COALESCE(details.amount, (0)::numeric)), (0)::numeric))::numeric(15,2)
                            ELSE (0)::numeric
                        END AS formation,
                        CASE
                            WHEN (details.expense_type_id = 3) THEN (COALESCE(sum(COALESCE(details.amount, (0)::numeric)), (0)::numeric))::numeric(15,2)
                            ELSE (0)::numeric
                        END AS frais_postaux,
                        CASE
                            WHEN (details.expense_type_id = 4) THEN (COALESCE(sum(COALESCE(details.amount, (0)::numeric)), (0)::numeric))::numeric(15,2)
                            ELSE (0)::numeric
                        END AS telecom,
                        CASE
                            WHEN (details.expense_type_id = ANY (ARRAY[5, 8, 11, 12, 13])) THEN (COALESCE(sum(COALESCE(details.amount, (0)::numeric)), (0)::numeric))::numeric(15,2)
                            ELSE (0)::numeric
                        END AS autres,
                    (reports.total_with_taxes)::numeric(15,2) AS ttc,
                    (reports.total)::numeric(15,2) AS ht,
                    (COALESCE(reports.vat, (0)::numeric))::numeric(15,2) AS tva
                   FROM (public.goxygene_expense_reports reports
                     JOIN public.goxygene_expense_report_details details ON ((reports.id = details.expense_report_id)))
                  GROUP BY reports.consultant_id, reports.date, reports.total_with_taxes, reports.total, reports.vat, details.expense_type_id) req
          GROUP BY req.id_consult, req.annee, req.mois, req.ttc, req.ht, req.tva) req2
  GROUP BY req2.id_consult, req2.annee, req2.mois;


