-- Name: vw_facturation_consultant_annee_client_itg_company; Type: VIEW; Schema: public; Owner: -
--

CREATE VIEW public.vw_facturation_consultant_annee_client_itg_company AS
 SELECT req.establishment_id,
    req.consultant_id,
    req.itg_company_id,
    req.annee,
    (sum(req.honoraires_euros))::numeric(18,2) AS honoraires_euros,
    (sum(req.frais_euros))::numeric(18,2) AS frais_euros,
    (sum(req.tva_euros))::numeric(18,2) AS tva_euros,
    (sum(req.total_ht_euros))::numeric(18,2) AS total_ht_euros,
    (sum(req.ttc_euros))::numeric(18,2) AS ttc_euros,
    (sum(req.part_consult_euros))::numeric(18,2) AS part_consult_euros,
    (sum(req.reste_du_euros))::numeric(18,2) AS reste_du_euros,
    (sum(req.frais_gestion))::numeric(18,2) AS frais_gestion
   FROM ( SELECT bills.establishment_id,
            bills.biller_id AS consultant_id,
            date_part('year'::text, bills.date) AS annee,
            (sum(
                CASE
                    WHEN (bills.bill_type = 'umbrella_bill'::public.customer_bill_type_enum) THEN bills.fees_euros
                    ELSE (- bills.fees_euros)
                END))::numeric(18,2) AS honoraires_euros,
            (sum(
                CASE
                    WHEN (bills.bill_type = 'umbrella_bill'::public.customer_bill_type_enum) THEN bills.expenses_euros
                    ELSE (- bills.expenses_euros)
                END))::numeric(18,2) AS frais_euros,
            sum(bills.vat_euros) AS tva_euros,
            sum((bills.total_including_taxes_euros - bills.vat_euros)) AS total_ht_euros,
            sum(bills.total_including_taxes_euros) AS ttc_euros,
            sum(dispatch.amount_euros) AS part_consult_euros,
            sum(bills.left_to_pay_euros) AS reste_du_euros,
            sum(
                CASE
                    WHEN (bills.bill_type = 'umbrella_credit_bill'::public.customer_bill_type_enum) THEN ((('-1'::integer)::numeric * (COALESCE(bills.fees_euros, (0)::numeric) + COALESCE(bills.expenses_euros, 0.0))) - COALESCE(dispatch.amount_euros, (0)::numeric))
                    ELSE ((COALESCE(bills.fees_euros, (0)::numeric) - COALESCE(dispatch.amount_euros, (0)::numeric)) + COALESCE(bills.expenses_euros, 0.0))
                END) AS frais_gestion,
            bills.itg_company_id
           FROM (public.goxygene_customer_bills bills
             LEFT JOIN public.goxygene_customer_bill_dispatchings dispatch ON (((bills.id = dispatch.customer_bill_id) AND (dispatch.dispatching = 'consultant_share'::public.customer_bill_dispatching_enum))))
          WHERE (((bills.biller_type)::text = 'Goxygene::Consultant'::text) AND (bills.status <> 'cancelled'::public.customer_bill_status_enum))
