    req.annee,
    (sum(req.honoraires_euros))::numeric(18,2) AS honoraires_euros,
    (sum(req.frais_euros))::numeric(18,2) AS frais_euros,
    (sum(req.tva_euros))::numeric(18,2) AS tva_euros,
