    (sum(req.part_consult_euros))::numeric(18,2) AS part_consult_euros,
    (sum(req.reste_du_euros))::numeric(18,2) AS reste_du_euros,
    (sum(req.frais_gestion))::numeric(18,2) AS frais_gestion
   FROM ( SELECT bills.establishment_id,
            bills.biller_id AS sous_traitant_id,
            date_part('year'::text, bills.date) AS annee,
            (sum(
                CASE
                    WHEN (bills.bill_type = 'client_bill'::public.customer_bill_type_enum) THEN bills.fees_euros
                    ELSE (- bills.fees_euros)
                END))::numeric(18,2) AS honoraires_euros,
