                    WHEN (bills.bill_type = 'client_credit_bill'::public.customer_bill_type_enum) THEN ((('-1'::integer)::numeric * (COALESCE(bills.fees_euros, (0)::numeric) + COALESCE(bills.expenses_euros, 0.0))) - COALESCE(dispatch.amount_euros, (0)::numeric))
