--
-- Name: vw_stats_annuelles_cir; Type: VIEW; Schema: public; Owner: -
--

CREATE VIEW public.vw_stats_annuelles_cir AS
 SELECT req.code,
    req.payroll_code AS matricule,
    req.competences,
    req.itg_company_id,
    req.code_groupe,
    req.id_corresp,
    req.id_commercial,
    req.designation,
    req.nb_salaires,
    req.annee,
    ( SELECT count(attempts.id) AS count
           FROM ((public.casino_login_attempts attempts
             JOIN public.casino_users cas ON ((attempts.user_id = cas.id)))
             JOIN public.cas_authentications authent ON ((((cas.username)::text = (authent.login)::text) AND ((authent.cas_user_type)::text = 'Goxygene::Consultant'::text) AND (authent.cas_user_id = req.code))))
          WHERE ((attempts.successful = true) AND (date_part('year'::text, attempts.created_at) = (req.annee)::double precision))) AS connexion_bureau,
    ( SELECT count(*) AS count
           FROM public.goxygene_activity_reports
          WHERE ((goxygene_activity_reports.activity_report_status = 'completed'::public.activity_report_status_enum) AND (goxygene_activity_reports.consultant_id = req.code) AND (goxygene_activity_reports.year = req.annee))) AS nb_da,
    ( SELECT count(*) AS count
           FROM public.goxygene_operating_expenses
          WHERE ((goxygene_operating_expenses.operating_expenses_status = ANY (ARRAY['itg_editing'::public.operating_expense_status_enum, 'itg_validated'::public.operating_expense_status_enum, 'completed'::public.operating_expense_status_enum])) AND (goxygene_operating_expenses.consultant_id = req.code) AND (goxygene_operating_expenses.year = req.annee))) AS nb_df,
    ( SELECT count(*) AS count
           FROM public.goxygene_expense_reports
          WHERE ((goxygene_expense_reports.consultant_id = req.code) AND (date_part('year'::text, goxygene_expense_reports.date) = (req.annee)::double precision))) AS nb_ndf,
    ( SELECT count(*) AS count
           FROM public.goxygene_office_customer_bills office_bills
          WHERE (((office_bills.biller_type)::text = 'Goxygene::Consultant'::text) AND (office_bills.biller_id = req.code) AND (date_part('year'::text, office_bills.date) = (req.annee)::double precision))) AS nb_demande_fact,
    ( SELECT count(*) AS count
           FROM public.goxygene_wage_advances
          WHERE ((goxygene_wage_advances.consultant_id = req.code) AND (date_part('year'::text, goxygene_wage_advances.date) = (req.annee)::double precision) AND ((goxygene_wage_advances.created_by = 254) OR (NOT (goxygene_wage_advances.created_by IN ( SELECT goxygene_employees.individual_id
                   FROM public.goxygene_employees)))))) AS nb_avances_bureau,
    ( SELECT count(*) AS count
           FROM public.goxygene_office_business_contracts
          WHERE ((goxygene_office_business_contracts.consultant_id = req.code) AND (date_part('year'::text, goxygene_office_business_contracts.begining_date) = (req.annee)::double precision))) AS nb_demande_ci,
    ( SELECT count(DISTINCT contracts.id) AS count
           FROM (public.goxygene_business_contracts contracts
             JOIN public.goxygene_business_contract_versions versions ON ((contracts.id = versions.business_contract_id)))
          WHERE (((contracts.commissioned_type)::text = 'Goxygene::Consultant'::text) AND (contracts.commissioned_id = req.code) AND (date_part('year'::text, versions.begining_date) = (req.annee)::double precision))) AS nb_contrat_interv,
    req2.jours_trav,
    req2.jours_dev,
    req3.nb_factures,
    req3.ca
   FROM ((( SELECT consultants.id AS code,
            compta.payroll_code,
            consultants.competences,
            consultants.itg_company_id,
            groups.analytical_axe AS code_groupe,
            consultants.correspondant_employee_id AS id_corresp,
            consultants.advisor_employee_id AS id_commercial,
            consultants.nickname AS designation,
            count(wages.id) AS nb_salaires,
            wages.year AS annee
           FROM (((public.goxygene_consultants consultants
             JOIN public.goxygene_consultant_accountancy_data compta ON ((consultants.id = compta.consultant_id)))
             LEFT JOIN public.goxygene_accountancy_consultant_groups groups ON ((compta.accountancy_consultant_group_id = groups.id)))
             JOIN public.goxygene_wages wages ON (((consultants.id = wages.consultant_id) AND (wages.disbursement_source_status = 'done'::public.disbursement_source_status_enum))))
          GROUP BY consultants.id, compta.payroll_code, consultants.competences, consultants.itg_company_id, groups.analytical_axe, consultants.correspondant_employee_id, consultants.advisor_employee_id, consultants.nickname, wages.year) req
     LEFT JOIN ( SELECT goxygene_activity_reports.consultant_id,
            goxygene_activity_reports.year,
            sum(COALESCE(goxygene_activity_reports.work_days, (0)::numeric)) AS jours_trav,
            sum(COALESCE(goxygene_activity_reports.enhancement_days, (0)::numeric)) AS jours_dev
           FROM public.goxygene_activity_reports
          WHERE (goxygene_activity_reports.activity_report_status = 'completed'::public.activity_report_status_enum)
          GROUP BY goxygene_activity_reports.consultant_id, goxygene_activity_reports.year) req2 ON (((req.code = req2.consultant_id) AND (req.annee = req2.year))))
     LEFT JOIN ( SELECT reqfact.consultant_id,
            reqfact.annee,
            sum(reqfact.nb_factures) AS nb_factures,
            sum(reqfact.ca) AS ca
           FROM ( SELECT goxygene_customer_bills.biller_id AS consultant_id,
                    date_part('year'::text, goxygene_customer_bills.date) AS annee,
                    count(goxygene_customer_bills.id) AS nb_factures,
                    (sum(
                        CASE
                            WHEN (goxygene_customer_bills.bill_type = 'umbrella_bill'::public.customer_bill_type_enum) THEN goxygene_customer_bills.fees_euros
                            ELSE (- goxygene_customer_bills.fees_euros)
                        END))::numeric(18,2) AS ca
                   FROM public.goxygene_customer_bills
                  WHERE (((goxygene_customer_bills.biller_type)::text = 'Goxygene::Consultant'::text) AND (goxygene_customer_bills.status <> 'cancelled'::public.customer_bill_status_enum))
                  GROUP BY goxygene_customer_bills.biller_id, goxygene_customer_bills.date) reqfact
          GROUP BY reqfact.consultant_id, reqfact.annee) req3 ON (((req.code = req3.consultant_id) AND ((req.annee)::double precision = req3.annee))))
  ORDER BY req.annee DESC, req.code;


